INSERT INTO `key_value_expire` VALUES ('announcements_feed','announcements',_binary 'a:8:{i:0;a:7:{s:2:\"id\";s:7:\"3465580\";s:5:\"title\";s:18:\"Drupal 11 released\";s:12:\"content_html\";s:114:\"<p>Today is a big day forÂ <a href=\"https://www.drupal.org/\">Drupal</a>Â as we officially released Drupal 11!</p>\n\";s:3:\"url\";s:46:\"https://www.drupal.org/blog/drupal-11-released\";s:13:\"date_modified\";s:25:\"2024-08-15T15:10:39+00:00\";s:14:\"date_published\";s:25:\"2024-08-01T22:57:58+00:00\";s:10:\"_drupalorg\";a:2:{s:8:\"featured\";b:0;s:7:\"version\";s:1:\"*\";}}i:2;a:7:{s:2:\"id\";s:7:\"3427138\";s:5:\"title\";s:52:\"You\'re invited to DrupalCon Portland | 6-9 May, 2024\";s:12:\"content_html\";s:384:\"<p>DrupalCon is an event for developers, marketers/content editors, site owners, and the Drupal-curious to join with the community. You\'ll find incredible keynotes, session content from expert speakers who have helped to build Drupal, networking opportunities with your peers, fun social events, and the chance to deepen your connection by contributing to the open source project.</p>\";s:3:\"url\";s:96:\"https://www.drupal.org/about/announcements/blog/youre-invited-to-drupalcon-portland-6-9-may-2024\";s:13:\"date_modified\";s:25:\"2024-03-18T19:40:04+00:00\";s:14:\"date_published\";s:25:\"2024-03-11T16:49:53+00:00\";s:10:\"_drupalorg\";a:2:{s:8:\"featured\";b:0;s:7:\"version\";s:5:\">=7.0\";}}i:3;a:7:{s:2:\"id\";s:7:\"3408836\";s:5:\"title\";s:28:\"Drupal 10.2 is now available\";s:12:\"content_html\";s:378:\"<p>The <a href=\"https://www.drupal.org/project/drupal/releases/10.2.0\">second feature release of Drupal 10</a> improves content modeling, block management, menu and taxonomy organization, and permission administration. New options to sanitize file names make it possible to clean up the names of uploaded files, and media item revisions now have a dedicated user interface.</p>\n\";s:3:\"url\";s:41:\"https://www.drupal.org/blog/drupal-10-2-0\";s:13:\"date_modified\";s:25:\"2024-01-25T18:24:28+00:00\";s:14:\"date_published\";s:25:\"2023-12-15T18:27:47+00:00\";s:10:\"_drupalorg\";a:2:{s:8:\"featured\";b:0;s:7:\"version\";s:5:\">=7.0\";}}i:4;a:7:{s:2:\"id\";s:7:\"3403229\";s:5:\"title\";s:75:\"Drupal 10 will be supported until the release of Drupal 12 in mid-late 2026\";s:12:\"content_html\";s:190:\"<p>Announcing a new major release schedule with long term support for Drupal 10 until mid-late 2026. Drupal 11 will be released in 2024 and work on the dedicated branch starts this week.</p>\";s:3:\"url\";s:103:\"https://www.drupal.org/blog/drupal-10-will-be-supported-until-the-release-of-drupal-12-in-mid-late-2026\";s:13:\"date_modified\";s:25:\"2024-01-25T18:31:05+00:00\";s:14:\"date_published\";s:25:\"2023-11-22T07:13:55+00:00\";s:10:\"_drupalorg\";a:2:{s:8:\"featured\";b:0;s:7:\"version\";s:5:\">=7.0\";}}i:6;a:7:{s:2:\"id\";s:7:\"3368367\";s:5:\"title\";s:28:\"Drupal 10.1 is now available\";s:12:\"content_html\";s:257:\"<p>This first feature release of Drupal 10 is packed with amazing improvements! It makes customizing the look of your site easier, adds support for decoupled navigation, improves content modeling and editing, block management, performance and many more.</p>\";s:3:\"url\";s:41:\"https://www.drupal.org/blog/drupal-10-1-0\";s:13:\"date_modified\";s:25:\"2023-10-30T16:22:24+00:00\";s:14:\"date_published\";s:25:\"2023-06-22T09:11:30+00:00\";s:10:\"_drupalorg\";a:2:{s:8:\"featured\";b:0;s:7:\"version\";s:5:\">=7.0\";}}i:7;a:7:{s:2:\"id\";s:7:\"3347976\";s:5:\"title\";s:37:\"Contribute to Drupal and the Open Web\";s:12:\"content_html\";s:146:\"<p>Drupal is an open source project. But what does that mean? How can you as a site-owner participate in the project of building a better web?</p>\";s:3:\"url\";s:85:\"https://www.drupal.org/about/announcements/blog/contribute-to-drupal-and-the-open-web\";s:13:\"date_modified\";s:25:\"2023-03-14T19:43:12+00:00\";s:14:\"date_published\";s:25:\"2023-03-14T19:39:44+00:00\";s:10:\"_drupalorg\";a:2:{s:8:\"featured\";b:0;s:7:\"version\";s:5:\">=7.0\";}}i:8;a:7:{s:2:\"id\";s:7:\"3343486\";s:5:\"title\";s:43:\"What to expect from the Announcements feed?\";s:12:\"content_html\";s:197:\"<p>Drupal is introducing a new project Announcements feature so that you can see news and updates about the Drupal project and community directly in your Drupal dashboard. Learn what to expect.</p>\";s:3:\"url\";s:90:\"https://www.drupal.org/about/announcements/blog/what-to-expect-from-the-announcements-feed\";s:13:\"date_modified\";s:25:\"2023-02-21T19:45:53+00:00\";s:14:\"date_published\";s:25:\"2023-02-21T19:45:53+00:00\";s:10:\"_drupalorg\";a:2:{s:8:\"featured\";b:0;s:7:\"version\";s:5:\">=7.0\";}}i:9;a:7:{s:2:\"id\";s:7:\"3327047\";s:5:\"title\";s:26:\"Drupal 10.0.0 is available\";s:12:\"content_html\";s:296:\"<p>Thanks to 2129 contributors from 616 organizations resolving 4083 issues in the past two and a half years, Drupal 10.0.0 is available today! This new version sets Drupal up for continued stability and security for the longer term. All new features will be added to Drupal 10 going forward.</p>\";s:3:\"url\";s:41:\"https://www.drupal.org/blog/drupal-10-0-0\";s:13:\"date_modified\";s:25:\"2023-10-30T16:21:38+00:00\";s:14:\"date_published\";s:25:\"2022-12-15T17:43:23+00:00\";s:10:\"_drupalorg\";a:2:{s:8:\"featured\";b:0;s:7:\"version\";s:5:\">=7.0\";}}}',1724392883),('system','advisories_response',_binary 'a:0:{}',1724328083);
